import '../App.scss';
import React from 'react';
import { useState, useEffect } from "react";
import Aos from 'aos';
import "aos/dist/aos.css";

function PhotoSlides() {
    const [slides, setSlides] = useState([])
    const [selectedPhoto, setSelectedPhoto] = useState('mechanical-technician')

    useEffect(()=>{
        Aos.init({duration: 2000});
      },[])
      useEffect(() => {
       fetch( "https://samochodowka-dd4f.restdb.io/rest/specializations" , {
          method: "GET",
          dataType: 'json',
          headers: {
            'cache-control': 'no-cache',
            'x-apikey': '61ad3d4572a03f5dae822144', 
      }
    })
    .then(response => response.json())
    .then(response => { 
        let list = []
        response.map( e => {
            let object = {
                name: e.name,
                photo: e.image,
                altText: e.altText
            }
            list.push(object);
        })
        setSlides(list);
        console.log(slides)
    })
      }, []);

  return (
    <div className="photo-slides">
        <div className="switch-container" >
     { slides.map(e => {
         return(
         <div key={e.photo} className={e.photo === selectedPhoto ? 'selected-button' : 'button'} ></div>)
     })}
     </div>
        <img src={`${process.env.PUBLIC_URL}/assets/${selectedPhoto.photo}.png`} alt={selectedPhoto.altText}/>
        <div className="title-container">
            <a>
                <h1>{selectedPhoto.name}</h1>
            </a>
        </div>
    </div>
  );
}

export default PhotoSlides;
